import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import PageWrapper from './PageWrapper';
import './Auth.css';

export default function Register() {
  const [validationErrors, setValidationErrors] = useState({});
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    startHour: 6,
    endHour: 23
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Automatically convert number fields to numbers
    const parsedValue = (name === "startHour" || name === "endHour") ? Number(value) : value;

    setFormData({ ...formData, [name]: parsedValue });
  };

  useEffect(() => {
    document.title = "Register - TaskMaster";
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        console.log('Registration successful');
        navigate('/login');
      } else if (res.status === 400) {
        const error = await res.json();
        setValidationErrors(error);
      } else {
        const error = await res.text();
        alert('Registration failed: ' + error);
      }
    } catch (err) {
      alert('Registration failed: ' + err.message);
    }
};

  return (
    <div className="register-background">
      <PageWrapper direction="right">
        <div className="form-container">
          <form onSubmit={handleSubmit}>
            <h2>Register</h2>

            <input
              type="text"
              name="username"
              placeholder="Username"
              value={formData.username}
              onChange={handleChange}
              required
            />
            {validationErrors.username && (
              <p className="validation-error">{validationErrors.username}</p>
            )}

            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            {validationErrors.email && (
              <p className="validation-error">{validationErrors.email}</p>
            )}

            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            {validationErrors.password && (
              <p className="validation-error">{validationErrors.password}</p>
            )}

            <label>
              Start Hour:
              <input
                type="number"
                name="startHour"
                min="0"
                max="23"
                value={formData.startHour}
                onChange={handleChange}
              />
            </label>

            <label>
              End Hour:
              <input
                type="number"
                name="endHour"
                min="0"
                max="23"
                value={formData.endHour}
                onChange={handleChange}
              />
            </label>
            {validationErrors.endHour && (
              <p className="validation-error">{validationErrors.endHour}</p>
            )}

            <div className="button-group">
              <button type="submit">Sign up</button>
              <button
                type="button"
                onClick={() => navigate('/')}
                className="w-full py-3 px-6 bg-indigo-600 text-white rounded-xl shadow hover:bg-indigo-700 transition duration-200"
              >
                ← Back to Welcome
              </button>
            </div>
          </form>
        </div>
      </PageWrapper>
    </div>
  );
}
