import { useState } from 'react';
import api from '../../services/api';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import PageWrapper from '../PageWrapper';
import { useEffect } from "react";



export default function Login() {

    const [formData, setFormData] = useState({ username: '', password: '' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    useEffect(() => {
        document.title = "Login - TaskMaster";
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("Login form submitted");
        try {
            const response = await fetch('http://localhost:8080/api/auth/login', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json'},
            body: JSON.stringify({
                username: formData.username,
                password: formData.password
                })
            });
            console.log("here");
            if (response.ok) {
                const data = await response.json();
                alert('Login success: ' + data.username + ' (' + data.userId + ')');
                localStorage.setItem("username", data.username);
                localStorage.setItem("userId", data.userId);
                localStorage.setItem("token", data.token);
                navigate('/dashboard');
            } else {
                alert('Login failed');
            }
        } catch (error) {
            console.error('Full error: ' + error);
            alert('Login failed: ' + error.message);
        }
    };

    return (
    <div className="login-background">
        <PageWrapper direction="left">
            <div className="form-container">
                <form onSubmit={handleSubmit}>            
                    <h2>Login</h2>
                    <input name="username" placeholder='Username' onChange={handleChange} required />
                    <input type="password" name="password" placeholder='Password' onChange={handleChange} required />
                    <div className="button-group">
                        <button type="submit">Login</button>
                        <button
                            onClick={() => navigate('/')}
                            className="w-full py-3 px-6 bg-indigo-600 text-white rounded-xl shadow hover:bg-indigo-700 transition duration-200" >
                            ‚Üê Back to Welcome
                        </button>
                    </div>
                </form>
            </div>
        </PageWrapper>
    </div>            
    );
}
